using namespace std;
const int maxn = 6005;
int n, root, r[maxn], f[maxn][2], v[maxn];  //f记录快乐指数总和；v表示visited，用于找root；r存储每个人的快乐指数
vector<int>s[maxn];
void dp(int x){
    f[x][0] = 0;
    f[x][1] = r[x];
    for(int i = 0; i < s[x].size(); i++){
        int t = s[x][i];
        dp(t);                             //dfs
        f[x][0] += max(f[t][0], f[t][1]);  //上司没来，直接下属想来就来
        f[x][1] += f[t][0];                //上司来了，直接下属一定不来
