```

**简易版**

```c++
#include <iostream>
#include <algorithm>
#include <cstring>
using namespace std;

const int maxn = 1001;

struct node{
    int c;
    int next;
}edge[maxn];

int low[maxn], dfn[maxn];
int stack[maxn], heads[maxn], visit[maxn]; //head配合结构体前向星
int cnt, dep, idx;

void addEdge(int x, int y){
    edge[++cnt].next = heads[x];
    edge[cnt].c = y;
    heads[x] = cnt;
}

void Tarjan(int u){  //代表第几个点在处理,递归的是点
    dfn[u] = low[u] = ++dep;  //新进点的初始化
    stack[++idx] = u;
    visit[u] = 1;
    for(int i = heads[u]; i != -1; i = edge[i].next){
        if(!dfn[edge[i].c]){
            Tarjan(edge[i].c);
            low[u] = min(low[u], low[edge[i].c]);  //递归出来，比较谁是谁的儿子／父亲，就是树的对应关系，涉及到强连通分量子树最小根的事情
        }
        else if(visit[edge[i].c]){
            low[u] = min(low[u], dfn[edge[i].c]);   //比较谁是谁的儿子／父亲,就是链接对应关系
        }
    }
    if(low[u] == dfn[u]){  //发现是整个强连通分量子树里的最小根
        do{
            cout << stack[idx] << " ";
            visit[stack[idx]] = 0;
            idx--;
        }while(u != stack[idx + 1]);
        cout << endl;
    }
}

int main(){
    memset(heads, -1, sizeof(heads));
    int n, m;
    cin >> n >> m;
    int x, y;
    for(int i = 0; i < m; i++){
        cin >> x >> y;
        addEdge(x, y);
    }
    for(int i = 1; i <= n; i++){
        if(!dfn[i])
            Tarjan(i);
    }
    return 0;
}

